INSERT INTO `rex_module` VALUES 
  (1,'Sprachtool - Uebersetzer',0,'<?php\r\n\r\n$catalogue_id = rex_post(\'catalogue_id\', \'integer\', 1);\r\n$category_id = rex_post(\'category_id\', \'array\', -1);\r\n\r\nif(rex_get(\'ajax-save\', \'boolean\'))\r\n{\r\n  $content =  \'\';\r\n    \r\n  $sql = rex_sql::factory();\r\n  $keyId = rex_post(\'keyId\', \'integer\');\r\n  $transArray = rex_post(\'trans\', \'array\');\r\n  \r\n  foreach($transArray as $locale => $translation)\r\n  {\r\n		$qry = \'SELECT id FROM rex_em_data_lang_locale WHERE locale=\"\'. $locale .\'\"\';\r\n		$sql->setQuery($qry);\r\n		\r\n		if($sql->getRows() == 1)\r\n		{\r\n			$localeId = $sql->getValue(\'id\');\r\n			\r\n			$qry = \'SELECT id FROM rex_em_data_lang_translations WHERE `key-id`=\'. $keyId .\' AND `locale-id`=\'. $localeId;\r\n			$sql->setQuery($qry);\r\n			$transId = $sql->getValue(\'id\');\r\n			\r\n			if($sql->getRows() == 0)\r\n			{\r\n				$qry = \'\r\n				INSERT INTO rex_em_data_lang_translations\r\n				SET `key-id`=\'. $keyId .\', `locale-id`=\'. $localeId .\',text=\"\'. $translation .\'\"\';\r\n				$sql->setQuery($qry);\r\n			}\r\n			else if($sql->getRows() == 1)\r\n			{\r\n				$qry = \'\r\n				UPDATE rex_em_data_lang_translations\r\n				SET `key-id`=\'. $keyId .\', `locale-id`=\'. $localeId .\',text=\"\'. $translation .\'\"\r\n				WHERE id=\'.$transId;\r\n				$sql->setQuery($qry);\r\n			}\r\n			else\r\n			{\r\n				$content .= \'Error with key \"\'. $keyId .\'\"!\';\r\n			}\r\n		}\r\n  }\r\n	\r\n  \r\n  // clear output-buffer\r\n  while(@ob_end_clean());\r\n\r\n  rex_send_resource($content);\r\n  exit();\r\n}\r\n\r\n\r\n$catalogue = new rex_select();\r\n$catalogue->setName(\'catalogue_id\');\r\n$catalogue->addSqlOptions(\'SELECT name,id FROM rex_em_data_lang_catalogue\');\r\n$catalogue->setAttribute(\'onchange\', \'this.form.submit()\');\r\n$catalogue->setSelected($catalogue_id);\r\n\r\n\r\n$catQry = \'\r\nSELECT name,id \r\nFROM rex_em_data_lang_category \r\nWHERE `catalogue-id` = \'. $catalogue_id . \' \r\nORDER BY section,prior,name\';\r\n\r\n$category = new rex_select();\r\n$category->setMultiple(true);\r\n$category->setName(\'category_id[]\');\r\n$category->addOption(\'Alle\', 0);\r\n$category->addSqlOptions($catQry);\r\n$category->setAttribute(\'onchange\', \'this.form.submit()\');\r\n$category->setSelected($category_id);\r\n\r\n// referenz sprache welche als Readonly neben jeder Sprache angezeigt wird\r\n$refLang = \'de_DE\';\r\n\r\n$qry = \'SELECT * FROM rex_em_data_lang_locale WHERE `catalogue-id`=\'.$catalogue_id;\r\n$sql = rex_sql::factory();\r\n$sql->setQuery($qry);\r\n\r\n$userLangs = array();\r\nwhile($sql->hasNext())\r\n{\r\n  // TODO: determine by user rights\r\n  $userLangs[] = $sql->getValue(\'locale\');\r\n  $sql->next();\r\n}\r\n\r\n// prepand refLang to all regular languages, if not already injected by the query\r\nif(!in_array($refLang, $userLangs))\r\n{\r\n  array_unshift($userLangs, $refLang);\r\n}\r\n\r\n$qry = \'\r\nSELECT\r\n  k.id,\r\n  k.name,\r\n  c.name AS catname\r\n  %%langCols%%\r\nFROM \r\n  rex_em_data_lang_keys k,\r\n  rex_em_data_lang_category c\r\nWHERE\r\n  k.`category-id` = c.id AND\r\n  c.`catalogue-id` = \'. $catalogue_id .\'\r\n  %%catCond%%\r\nORDER BY\r\n  c.section,\r\n  c.prior,\r\n  c.name\r\n\';\r\n\r\n$langCol = \'\';\r\nforeach($userLangs as $userLang)\r\n{\r\n  $langCol .= \',\r\n               (SELECT text FROM rex_em_data_lang_translations t \r\n                WHERE t.`key-id`=k.id AND t.`locale-id`=(SELECT id FROM rex_em_data_lang_locale WHERE locale=\"\'. $userLang .\'\"))\r\n        AS \"\'. $userLang .\'\"\';\r\n}\r\n\r\n$catCond = \'\';\r\nif(is_array($category_id))\r\n{\r\n  if(in_array(0, $category_id))\r\n  {\r\n    // nichts tun, alles wird angezeigt\r\n  }\r\n  else\r\n  {\r\n    // multiple category select\r\n    $catCond = \' AND c.id IN(\'. implode(\',\', $category_id) .\')\';\r\n  }\r\n}\r\nelse if($category_id < 0)\r\n{\r\n    // keine treffer anzeigen\r\n    $catCond = \'AND 1=2\';\r\n}\r\n\r\n\r\n$qry = str_replace(\'%%langCols%%\', $langCol, $qry);\r\n$qry = str_replace(\'%%catCond%%\', $catCond, $qry);\r\n\r\n$sql = rex_sql::factory();\r\n// $sql->debugsql = true;\r\n$sql->setQuery($qry);\r\n\r\necho \'<form action=\"index.php\" method=\"post\">\';\r\necho \'<input type=\"hidden\" name=\"article_id\" value=\"REX_ARTICLE_ID\" />\';\r\necho \'<input type=\"hidden\" name=\"clang\" value=\"REX_CLANG_ID\" />\';\r\necho \'<label>Sprachkatalog</label>\';\r\necho $catalogue->get();\r\necho \'<label>Kategorie</label>\';\r\necho $category->get();\r\necho \'</form>\';\r\n\r\necho \'<table border=1 class=\"ltool\">\';\r\n\r\necho \'<thead>\';\r\necho \'<tr>\';\r\necho \'<th>Schluessel</th>\';\r\nforeach($userLangs as $userLang)\r\n{\r\n  echo \'<th>\'. $userLang .\'</th>\';\r\n}\r\necho \'<th>Funktionen</th>\';\r\necho \'</tr>\';\r\necho \'</thead>\';\r\n\r\n\r\n\r\necho \'<tfoot>\';\r\necho \'</tfoot>\';\r\n\r\n\r\n\r\n$cat = \'\';\r\nwhile($sql->hasNext())\r\n{\r\n  if($cat != $sql->getValue(\'catname\'))\r\n  {\r\n    echo \'<tbody class=\"section\">\';\r\n    echo \'<tr>\';\r\n    echo \'<th colspan=\"\'. (count($userLangs)+2) .\'\">\'. htmlspecialchars($sql->getValue(\'catname\')) .\'</th>\';\r\n    echo \'</tr>\';\r\n  }\r\n  \r\n  echo \'<tr>\';\r\n  echo \r\n    \'<th>\'. \r\n	  $sql->getValue(\'name\') .\r\n	  \'<input type=\"hidden\" value=\"\'. $sql->getValue(\'id\') .\'\" name=\"keyId\" />\'.\r\n    \'</th>\';\r\n  \r\n  foreach($userLangs as $userLang)\r\n  {\r\n    echo \'\r\n    <td>\r\n      <input type=\"text\" value=\"\'. htmlspecialchars($sql->getValue($userLang)) .\'\" name=\"trans[\'. $userLang .\']\" />\r\n    </td>\';\r\n  }\r\n  \r\n  echo \'\r\n  <td>\r\n    <a href=\"#\" class=\"ltool-action-save\">Speichern</a>\r\n  </td>\';\r\n//    <a href=\"#\" class=\"ltool-action-revert\">Aenderung verwerfen</a>\r\n  \r\n  echo \'</tr>\';\r\n  \r\n  if($cat != $sql->getValue(\'catname\'))\r\n  {\r\n    echo \'</tbody>\';\r\n    $cat = $sql->getValue(\'catname\');\r\n  }\r\n  $sql->next();\r\n}\r\necho \'</table>\';\r\n\r\n?>\r\n<script type=\"text/javascript\">\r\n<!--\r\n  jQuery(function($) {\r\n	  $(\".ltool-action-save\").click(function (){\r\n		var $row = $(this).closest(\"tr\");\r\n		var values = [];\r\n		$(\":text,:hidden\", $row).each(function(){\r\n			  values.push({\'name\':$(this).attr(\'name\'),\'value\':$(this).val()});\r\n			  $(this).css(\'background-color\', \'white\');\r\n		});\r\n			\r\n		$.post(\"index.php?article_id=REX_ARTICLE_ID&clang=REX_CLANG_ID&ajax-save=1\", values);\r\n		return false;\r\n	   });\r\n	   \r\n	   $(\":text\").change(function(){\r\n		 $(this).css(\'background-color\', \'yellow\');\r\n	   });\r\n	   \r\n	   $(\":text\").filter(\'[value=\"\"]\').css(\'background-color\', \'wheat\');\r\n  });\r\n-->\r\n</script>','','admin','admin',1272537456,1272629371,'',0),
  (2,'Sprachtool - Uebersetzungsimport',0,'<?php\r\n\r\necho \"Adminfunktion - Uebersetzungsimport\";\r\n\r\n$uplFile = rex_files(\'impfile\', \'array\');\r\n$locale_id = rex_post(\'locale_id\', \'integer\');\r\n$catalogue_id = rex_post(\'catalogue_id\', \'integer\');\r\n\r\nif(!empty($uplFile))\r\n{\r\n  echo \'verarbeite upload...<br />\';\r\n\r\n  if(is_uploaded_file($uplFile[\'tmp_name\']))\r\n  {\r\n    echo \'datei validiert...<br />\';\r\n\r\n    $i18n = new i18n(\'\',\'\');\r\n    $i18n->appendFileName($uplFile[\'tmp_name\']);\r\n\r\n    $sql = rex_sql::factory();\r\n//    $sql->debugsql = true;\r\n    $keySql = rex_sql::factory();\r\n//    $keySql->debugsql = true;\r\n\r\n    foreach($i18n->text as $key => $value)\r\n    {\r\n      $qry = \'\r\n      SELECT id FROM rex_em_data_lang_keys WHERE name=\"\'. addslashes($key) .\'\"\r\n      \';\r\n      $keySql->setQuery($qry);\r\n\r\n      if($keySql->getRows() == 1)\r\n      {\r\n        $qry = \'\r\n          SELECT * FROM rex_em_data_lang_translations \r\n          WHERE\r\n            `key-id`=\'. $keySql->getValue(\'id\') .\' AND\r\n            `locale-id`=\'. $locale_id;\r\n        \r\n        $sql->setQuery($qry);\r\n\r\n        if($sql->getRows() == 0)\r\n        {\r\n          $qry = \'\r\n          INSERT INTO rex_em_data_lang_translations \r\n          SET\r\n            `key-id`=\'. $keySql->getValue(\'id\') .\',\r\n            `locale-id`=\'. $locale_id .\',\r\n            `text`=\"\'. addslashes($value) .\'\"\r\n          \';\r\n          $sql->setQuery($qry);\r\n        }\r\n        else if ($sql->getRows() == 1)\r\n        {\r\n          $qry = \'\r\n          UPDATE rex_em_data_lang_translations \r\n          SET\r\n            `text`=\"\'. addslashes($value) .\'\"\r\n          WHERE\r\n            `key-id`=\'. $keySql->getValue(\'id\') .\' AND\r\n            `locale-id`=\'. $locale_id;\r\n          \r\n          $sql->setQuery($qry);\r\n        }\r\n        else\r\n        {\r\n          echo \'Error multiple (\'. $sql->getRows() .\'x) translations found for key \"\'. $key .\'\"...!<br />\';\r\n        }\r\n      }\r\n      else\r\n      {\r\n        echo \'Error key \"\'. $key .\'\" not found...!<br />\';\r\n      }\r\n    }\r\n  }\r\n}\r\n\r\n$catalogue = new rex_select();\r\n$catalogue->setName(\'catalogue_id\');\r\n$catalogue->setSelected($catalogue_id);\r\n$catalogue->setAttribute(\'onchange\', \'this.form.submit()\');\r\n$catalogue->addSqlOptions(\'SELECT CONCAT(name),id FROM rex_em_data_lang_catalogue\');\r\n\r\nif($catalogue_id > 0)\r\n{\r\n	$locale = new rex_select();\r\n	$locale->setName(\'locale_id\');\r\n    $locale->setSelected($locale_id);\r\n	$locale->setAttribute(\'onchange\', \'this.form.submit()\');\r\n	$locale->addSqlOptions(\'SELECT CONCAT(locale,\" \",name),id FROM rex_em_data_lang_locale WHERE `catalogue-id`=\'.$catalogue_id);\r\n}\r\n\r\necho \'<form method=\"post\" enctype=\"multipart/form-data\">\';\r\necho \'<input type=\"hidden\" name=\"article_id\" value=\"REX_ARTICLE_ID\" />\';\r\necho \'<input type=\"hidden\" name=\"clang\" value=\"REX_CLANG_ID\" />\';\r\necho $catalogue->get();\r\n\r\nif($catalogue_id > 0)\r\n{\r\n	echo $locale->get();\r\n}\r\n\r\nif($catalogue_id > 0 && $locale_id > 0)\r\n{\r\n	echo \'<input type=\"file\" name=\"impfile\" />\';\r\n}\r\n\r\necho \'<input type=\"submit\" name=\"Upload\" />\';\r\necho \'</form>\';\r\n\r\n?>','','admin','admin',1272542124,1272628505,'',0),
