  (4,'Sprachtool - Sprachstatistik',0,'<?php\r\n\r\n$catalogue_id = rex_post(\'catalogue_id\', \'integer\', -1);\r\n\r\nif($catalogue_id > 0)\r\n{\r\n	echo \"Sprachstatistik\";\r\n\r\n	$qry = \'SELECT * FROM rex_em_data_lang_locale WHERE `catalogue-id`=\'.$catalogue_id;\r\n	$sql = rex_sql::factory();\r\n	$sql->setQuery($qry);\r\n\r\n 	$cntSql = rex_sql::factory();\r\n	// $cntSql->debugsql = true;\r\n	\r\n    $cntQry = \'\r\n	 SELECT count(*) as count \r\n	 FROM rex_em_data_lang_keys k, rex_em_data_lang_category c \r\n	 WHERE k.`category-id`=c.id AND c.`catalogue-id`=\'. $catalogue_id;\r\n    $cntSql->setQuery($cntQry);\r\n	$countKeys = $cntSql->getValue(\'count\');\r\n	\r\n	echo \'<table class=\"ltool_stats\">\';\r\n	while($sql->hasNext())\r\n	{\r\n      $cntQry = \'SELECT count(*) as count FROM rex_em_data_lang_translations WHERE `locale-id`=\'.$sql->getValue(\'id\') .\' AND text != \"\"\';\r\n	  $cntSql->setQuery($cntQry);\r\n	  $countTrans = $cntSql->getValue(\'count\');\r\n	  \r\n	  $procent = 0;\r\n	  if($countTrans > 0)\r\n	  {\r\n		$procent = round($countTrans / $countKeys, 2) * 100;\r\n	  }\r\n	  \r\n	  echo \'<tr>\';\r\n	  echo   \'<th>\'. htmlspecialchars($sql->getValue(\'name\').\' \'.$sql->getValue(\'locale\')) .\'</th>\';\r\n	  echo   \'<td> (\'. $countTrans .\'/\'. $countKeys .\') \'. $procent .\'% </td>\';\r\n	  echo \'</tr>\';\r\n	  $sql->next();\r\n	}\r\n	echo \'</table>\';\r\n}\r\n\r\n?>','','admin','admin',1272614193,1272628409,'',0),
